mapper.select:
SELECT id, username, content, url, browser, ip, ip_address AS ipAddress, log_level AS logLevel, business_type AS businessType, exception_content AS exceptionContent, request_param AS requestParam, time_consuming AS timeConsuming, remark, isvalid, ctime, creater, mtime, updater FROM sys_operate_log WHERE isvalid = 1
AND username LIKE concat('%', #{dto.username}, '%')
AND log_level = #{dto.logLevel}
AND business_type = #{dto.businessType}
AND url LIKE concat('%', #{dto.url}, '%')
AND ip_address LIKE concat('%', #{dto.ipAddress}, '%')
ORDER BY ctime DESC LIMIT #{pageParam.startIndex}, #{pageParam.pageSize}

SELECT count(1) FROM sys_operate_log WHERE isvalid = 1
AND username LIKE concat('%', #{dto.username}, '%')
AND log_level = #{dto.logLevel}
AND business_type = #{dto.businessType}
AND url LIKE concat('%', #{dto.url}, '%')
AND ip_address LIKE concat('%', #{dto.ipAddress}, '%')
