mapper.select:
SELECT id, menu_name AS menuName, menu_code AS menuCode, menu_type AS menuType, menu_parent_id AS menuParentId, remark, isvalid, ctime, creater, mtime, updater FROM sys_menu WHERE isvalid = 1
AND menu_name LIKE concat('%', #{param.menuName}, '%')
AND menu_code LIKE concat('%', #{param.menuCode}, '%')
AND menu_type = #{param.menyType}
AND menu_type = #{param.menuParentId}
LIMIT #{pageParam.startIndex}, #{pageParam.pageSize}
