mapper.select:
SELECT count(1)
FROM sys_role
WHERE isvalid = 1
    AND role_code = #{roleCode}

    AND id != #{id}

SELECT id, role_name AS roleName, role_code AS roleCode, is_admin AS isAdmin, remark, isvalid, ctime, creater, mtime, updater FROM sys_role WHERE isvalid = 1
AND role_code LIKE concat('%', #{dto.roleCode}, '%')
AND role_name LIKE concat('%', #{dto.roleName}, '%')
AND is_admin = #{dto.isAdmin}
ORDER BY role_name LIMIT #{pageParam.startIndex}, #{pageParam.pageSize}
