mapper.select:
SELECT id, org_id AS orgId, app_code AS appCode, app_name AS appName, app_type AS appType, remark, isvalid, ctime, creater, mtime, updater FROM service_application WHERE isvalid = 1
AND org_id = #{dto.orgId}
AND app_code LIKE concat('%', #{dto.appCode}, '%')
AND app_name LIKE concat('%', #{dto.appName}, '%')
AND app_type = #{dto.appType}
ORDER BY app_code LIMIT #{pageParam.startIndex}, #{pageParam.pageSize}
